document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[id^='seeMoreDescriptionBtn-']").forEach(e=>{const s=e.id.replace("seeMoreDescriptionBtn-",""),t=document.getElementById(`descriptionText-${s}`);e.addEventListener("click",()=>{t.classList.toggle("line-clamp-3"),e.textContent=t.classList.contains("line-clamp-3")?"See More":"See Less"})}),document.querySelectorAll("[id^='seeMoreBtn-']").forEach(e=>{const s=e.id.replace("seeMoreBtn-",""),t=document.getElementById(`keyImpactText-${s}`);e.addEventListener("click",()=>{t.classList.toggle("collapsed"),e.textContent=t.classList.contains("collapsed")?"See More":"See Less"})})});
